mapper.update:
UPDATE
canbo_b2b_processlog
SET
	process_flag    = #{processFlag},
	update_date     = #{updateDt},
	result_json = #{resultJson}

		,process_comment = #{processComment}

WHERE
	id= #{id}

mapper.select:
SELECT id, interface_name, info_json, result_json, data_source, process_flag, process_comment, create_by, create_date as 'createDt' FROM canbo_b2b_processlog
AND data_source = #{processLogSearchModel.dataSource}
AND interface_name = #{code}
AND process_flag =#{processLogSearchModel.processFlag}
AND create_date >= #{processLogSearchModel.beginCreateDt}
AND create_date <= #{processLogSearchModel.endCreateDt}
ORDER BY create_date DESC
